import React, { useState } from 'react';
import './App.css';
import EventAnalyzer from './components/EventAnalyzer';
import NavigationBar from './components/NavigationBar';
import DataDisplay from './components/DataDisplay';
import ScheduleDisplay from './components/ScheduleDisplay';

function App() {
  const [eventData, setEventData] = useState(null);
  const [scheduleData, setScheduleData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [currentEvent, setCurrentEvent] = useState(null);
  const [activeTab, setActiveTab] = useState('teams');

  const handleEventAnalysis = async (eventCode, season) => {
    setLoading(true);
    setError(null);
    setEventData(null);
    setScheduleData(null);
    setActiveTab('teams');
    setCurrentEvent({ code: eventCode, season });

    try {
      const response = await fetch(`/api/event/${eventCode}?season=${season}`);
      if (!response.ok) {
        throw new Error('Failed to fetch event data');
      }
      const data = await response.json();
      setEventData(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleGenerateSchedule = async () => {
    if (!currentEvent) return;

    setLoading(true);
    setError(null);

    try {
      const response = await fetch(
        `/api/generate-schedule/${currentEvent.code}?season=${currentEvent.season}`
      );
      if (!response.ok) {
        throw new Error('Failed to generate schedule');
      }
      const data = await response.json();
      setScheduleData(data);
      setActiveTab('schedule');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>ðŸ¤– CSH Predict - Team Performance Analysis</h1>
        <p>Analyze FTC team performance by event</p>
      </header>

      <div className="container">
        <EventAnalyzer onAnalyze={handleEventAnalysis} loading={loading} />

        {error && <div className="error-message">Error: {error}</div>}

        {loading && <div className="loading-message">Loading data...</div>}

        {eventData && (
          <>
            <NavigationBar 
              activeTab={activeTab} 
              onTabChange={setActiveTab}
              hasSchedule={true}
            />

            {activeTab === 'teams' && (
              <>
                <DataDisplay data={eventData} />
              </>
            )}

            {activeTab === 'schedule' && (
              <>
                <div className="action-buttons">
                  <button 
                    className="generate-btn"
                    onClick={handleGenerateSchedule}
                    disabled={loading}
                  >
                    ðŸ“‹ GenereazÄƒ Program
                  </button>
                </div>
                {scheduleData ? (
                  <ScheduleDisplay
                    schedule={scheduleData.schedule}
                    duplicates={scheduleData.duplicate_alliances}
                    matchesCount={scheduleData.matches_count}
                    eventName={scheduleData.event_name}
                  />
                ) : (
                  <div className="empty-schedule">
                    <p>ApasÄƒ butonul pentru a genera programul evenimentului</p>
                  </div>
                )}
              </>
            )}
          </>
        )}
      </div>

      <footer className="App-footer">
        <p>Powered by FTC Scout API</p>
      </footer>
    </div>
  );
}

export default App;
