import React, { useState } from 'react';
import './App.css';
import EventAnalyzer from './components/EventAnalyzer';
import DataDisplay from './components/DataDisplay';

function App() {
  const [eventData, setEventData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleEventAnalysis = async (eventCode, season) => {
    setLoading(true);
    setError(null);
    setEventData(null);

    try {
      const response = await fetch(`/api/event/${eventCode}?season=${season}`);
      if (!response.ok) {
        throw new Error('Failed to fetch event data');
      }
      const data = await response.json();
      setEventData(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>ðŸ¤– CSH Predict - Team Performance Analysis</h1>
        <p>Analyze FTC team performance by event</p>
      </header>

      <div className="container">
        <EventAnalyzer onAnalyze={handleEventAnalysis} loading={loading} />

        {error && <div className="error-message">Error: {error}</div>}

        {loading && <div className="loading-message">Loading data...</div>}

        {eventData && <DataDisplay data={eventData} />}
      </div>

      <footer className="App-footer">
        <p>Powered by FTC Scout API</p>
      </footer>
    </div>
  );
}

export default App;
